function Ranker(div,rawData,rankingCode,representFunc,dataProcessFunction){this.div=div,this.rawData=rawData,this.rankingFunction=eval(rankingCode),this.representFunc=representFunc,this.shouldProcess=!0,"undefined"==typeof dataProcessFunction?this.shouldProcess=!1:this.processData=dataProcessFunction}Ranker.prototype.updateData=function(t){this.rawData=t,this.data=this.shouldProcess?this.processData(t):t},Ranker.prototype.updateCode=function(newCode){this.rankingFunction=eval("this.rankingFunction = "+newCode)},Ranker.prototype.run=function(){for(;this.div.firstChild;)this.div.removeChild(this.div.firstChild);result=this.rankingFunction(this.data);for(var t=0;t<result.length;t++)this.div.appendChild(this.representFunc(t,result[t]))};